#include "StdAfx.h"
#include "UIEvents.h"

CModuleUIEvents::CModuleUIEvents() : 
	pEvents(nullptr)
{
	CryLog("Register UI events...");

	if (gEnv->pFlashUI)
	{
		pEvents = gEnv->pFlashUI->CreateEventSystem("Test", IUIEventSystem::eEST_SYSTEM_TO_UI);

		m_eventSender.Init(pEvents);

		{
			SUIEventDesc eventDesc("OnError",  "Triggered if client recived error from online server");
			eventDesc.AddParam<SUIParameterDesc::eUIPT_String>("Error", "Error string");
			m_eventSender.RegisterEvent<eUIGE_OnError>(eventDesc);
		}

		CryLog("UI events registered. Events count (%d)", pEvents->GetEventCount());
	}
}
