    private function renderText(scale:Number, resultTextBounds:Rectangle):BitmapData
    {
        var width:Number  = mHitArea.width  * scale;
        var height:Number = mHitArea.height * scale;
        var hAlign:String = mHAlign;
        var vAlign:String = mVAlign;

        if (isHorizontalAutoSize)
        {
            width = int.MAX_VALUE;
            hAlign = HAlign.LEFT;
        }
        if (isVerticalAutoSize)
        {
            height = int.MAX_VALUE;
            vAlign = VAlign.TOP;
        }

        var textFormat:TextFormat = new TextFormat(mFontName, 
            mFontSize * scale, mColor, mBold, mItalic, mUnderline, null, null, hAlign);
        textFormat.kerning = mKerning;

        sNativeTextField.defaultTextFormat = textFormat;
        sNativeTextField.width = width;
        sNativeTextField.height = height;
        sNativeTextField.antiAliasType = AntiAliasType.ADVANCED;
        sNativeTextField.selectable = false;            
        sNativeTextField.multiline = true;            
        sNativeTextField.wordWrap = true;            
        sNativeTextField.text = mText;

        if ( sEmbedFontNames[mFontName] == null )
            sNativeTextField.embedFonts = true;
        else
            sNativeTextField.embedFonts = sEmbedFontNames[mFontName];

        sNativeTextField.filters = mNativeFilters;

        // we try embedded fonts first, non-embedded fonts are just a fallback
        if (sNativeTextField.embedFonts == true && ( sNativeTextField.textWidth == 0.0 || sNativeTextField.textHeight == 0.0 ))
        {
            sNativeTextField.embedFonts = false;
            sEmbedFontNames[mFontName] = false;
        }

        formatText(sNativeTextField, textFormat);

        if (mAutoScale)
            autoScaleNativeTextField(sNativeTextField);

        var textWidth:Number  = sNativeTextField.textWidth;
        var textHeight:Number = sNativeTextField.textHeight;

        if (isHorizontalAutoSize)
            sNativeTextField.width = width = Math.ceil(textWidth + 5);
        if (isVerticalAutoSize)
            sNativeTextField.height = height = Math.ceil(textHeight + 4);

        // avoid invalid texture size
        if (width  < 1) width  = 1.0;
        if (height < 1) height = 1.0;

        var xOffset:Number = 0.0;
        if (hAlign == HAlign.LEFT)        xOffset = 2; // flash adds a 2 pixel offset
        else if (hAlign == HAlign.CENTER) xOffset = (width - textWidth) / 2.0;
        else if (hAlign == HAlign.RIGHT)  xOffset =  width - textWidth - 2;

        var yOffset:Number = 0.0;
        if (vAlign == VAlign.TOP)         yOffset = 2; // flash adds a 2 pixel offset
        else if (vAlign == VAlign.CENTER) yOffset = (height - textHeight) / 2.0;
        else if (vAlign == VAlign.BOTTOM) yOffset =  height - textHeight - 2;

        //var bitmapData:BitmapData = new BitmapData(width, height, true, 0x0);
        if ( sBitmapData != null )
        {
            if ( sBitmapData.width < width || sBitmapData.height < height ) 
            {
                sBitmapData.dispose();
                sBitmapData = null;
            }
            else
                sBitmapData.fillRect(sBitmapData.rect, 0);
        }
        if ( sBitmapData == null )
            sBitmapData = new BitmapData(width, height, true, 0x0);

        var drawMatrix:Matrix = new Matrix(1, 0, 0, 1, 0, int(yOffset) - 2); 

        // Beginning with AIR 3.3, we can force a drawing quality. Since "LOW" produces
        // wrong output oftentimes, we force "MEDIUM" if possible.
        if ( sHasTestedForDrawWithQuality == false )
        {
            sHasTestedForDrawWithQuality = true;

            var drawWithQualityFunc:Function = 
                "drawWithQuality" in sBitmapData ? sBitmapData["drawWithQuality"] : null;
            if ( drawWithQualityFunc )
                sHasDrawWithQuality = true;
        }
        if  ( sHasDrawWithQuality )
        {
            sBitmapData.drawWithQuality(sNativeTextField, drawMatrix, 
                                     null, null, null, false, StageQuality.MEDIUM);
        }
        else
            sBitmapData.draw(sNativeTextField, drawMatrix);

        sNativeTextField.text = "";

        // update textBounds rectangle
        resultTextBounds.setTo(xOffset   / scale, yOffset    / scale,
                               textWidth / scale, textHeight / scale);

        return sBitmapData;
    }
```

