    public static void main(String[] args) {
            boolean resultBug;
            boolean result;

            // Test isCollinear Vector2
            Vector2 v1 = new Vector2(1, 2);
            Vector2 v2 = new Vector2(1, 2);

            // true
            resultBug = v1.isCollinear(v2, epsilon);
            result = isCollinear(v1, v2, epsilon);
            display(v1, v2, resultBug, result);

            // True
            v2.set(-1, -2);
            resultBug = v1.isCollinear(v2, epsilon);
            result = isCollinear(v1, v2, epsilon);
            display(v1, v2, resultBug, result);

            // True
            v2.set(2, 4);
            resultBug = v1.isCollinear(v2, epsilon);
            result = isCollinear(v1, v2, epsilon);
            display(v1, v2, resultBug, result);

            // True
            v2.set(200, 400);
            resultBug = v1.isCollinear(v2, epsilon);
            result = isCollinear(v1, v2, epsilon);
            display(v1, v2, resultBug, result);

            // False
            v2.set(1, 3);
            resultBug = v1.isCollinear(v2, epsilon);
            result = isCollinear(v1, v2, epsilon);
            display(v1, v2, resultBug, result);

            // Test Vector3
            // Test isCollinear Vector3
            Vector3 v31 = new Vector3(1, 2, 3);
            Vector3 v32 = new Vector3(1, 2, 3);

            // true
            resultBug = v31.isCollinear(v32, epsilon);
            result = isCollinear(v31, v32, epsilon);
            display(v31, v32, resultBug, result);

            // True
            v32.set(-1, -2, -3);
            resultBug = v31.isCollinear(v32, epsilon);
            result = isCollinear(v31, v32, epsilon);
            display(v31, v32, resultBug, result);

            // True
            v32.set(2, 4, 6);
            resultBug = v31.isCollinear(v32, epsilon);
            result = isCollinear(v31, v32, epsilon);
            display(v31, v32, resultBug, result);

            // True
            v32.set(200, 400, 600);
            resultBug = v31.isCollinear(v32, epsilon);
            result = isCollinear(v31, v32, epsilon);
            display(v31, v32, resultBug, result);

            // False
            v32.set(1, 2, 4);
            resultBug = v31.isCollinear(v32, epsilon);
            result = isCollinear(v31, v32, epsilon);
            display(v31, v32, resultBug, result);
