    public static String format(Vector2 v) {
            return "(" + dfmt.format(v.x) + "," + dfmt.format(v.y) + ")";

    }

    public static String format(Vector3 v) {
            return "(" + dfmt.format(v.x) + "," + dfmt.format(v.y) + "," + dfmt.format(v.z) + ")";

    }

    public static void display(Vector2 v1, Vector2 v2, boolean bug, boolean fixed) {
            System.out.println("Vector2 " + format(v1) + " " + format(v2) + " are Collinear: " + bug + " " + fixed + (fixed != bug ? " *** BUG ***" : " OK"));

    }

    public static void display(Vector3 v1, Vector3 v2, boolean bug, boolean fixed) {
            System.out.println("Vector3 " + format(v1) + " " + format(v2) + " are Collinear: " + bug + " " + fixed + (fixed != bug ? " *** BUG ***" : " OK"));

    }

    /**
     * Correct implementation of isCollinear
     * 
     * 
     * @param v1
     * @param v2
     * @param epsilon
     * @return
     */
    public static boolean isCollinear(Vector3 v1, Vector3 v2, float epsilon) {
            Vector3 v1copy = v1.cpy();

            v1copy = v1copy.crs(v2);
            return v1copy.len() <= epsilon;
    }

    public static boolean isCollinear(Vector3 v1, Vector3 v2) {
            return isCollinear(v1, v2, 0f);
    }

    /**
     * Correct implementation of isCollinear Vector2
     * 
     * Calculate area of three points p1: (0,0,0) p2:vector1 p3: vector2
     * 
     * Area of a triangle is [ p1.x * (P2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y) ] / 2
     * Since p0(x,y,z) are always zeroes we can simplify
     * [0 * (P2.y - p3.y) + p2.x * (p3.y - 0) + p3.x * (0 - p2.y) ] / 2
     * [0 * (P2.y - p3.y) + p2.x * (p3.y - 0) + p3.x * (0 - p2.y) ] / 2
     * [p2.x * p3.y + p3.x * -p2.y]/2
     * 
     * since we expect 0 no need to divide by 2
     * 
     * [p2.x * p3.y + p3.x * -p2.y] == 0 means collinearity
     * 
     * 
     * @param v1
     * @param v2
     * @param epsilon
     *            error level (e.g. 0.000000001) to account for rounding errors in java.
     * @return
     */
    public static boolean isCollinear(Vector2 v1, Vector2 v2, float epsilon) {

            return Math.abs(v1.x * v2.y + v2.x * -v1.y) <= epsilon;
    }

    public static boolean isCollinear(Vector2 v1, Vector2 v2) {
            return isCollinear(v1, v2, 0f);
    }
