Example: Note that rotation is not 0 at the end of this Tween

```
package com.me.gdxlearn;

import com.badlogic.gdx.ApplicationListener;
import com.badlogic.gdx.Gdx;
import com.badlogic.gdx.graphics.GL20;
import com.badlogic.gdx.graphics.Texture;

import com.badlogic.gdx.scenes.scene2d.Actor;
import com.badlogic.gdx.scenes.scene2d.Stage;
import com.badlogic.gdx.scenes.scene2d.ui.Image;

import aurelienribon.tweenengine.Tween;
import aurelienribon.tweenengine.TweenAccessor;
import aurelienribon.tweenengine.TweenManager;

public class GdxLearn implements ApplicationListener {

    Stage stage;

    public class ActorAccessor implements TweenAccessor<Actor> {

        public static final int ROTATE = 1;

        @Override
        public int getValues(Actor target, int tweenType, float[] returnValues) {
            switch (tweenType) {
                case ROTATE:
                    returnValues[0] = target.getScaleX();
                    return 1;   
            }
            return 0;
        }

        @Override
        public void setValues(Actor target, int tweenType, float[] newValues) {
            switch (tweenType) {
                case ROTATE:
                    target.setRotation(newValues[0]);
                    break;  
            }
        }
    }

    static final private TweenManager m_tween_manager = new TweenManager();

    static TweenManager getTweenManager() {
        return m_tween_manager;
    }

        @Override
    public void create () {     
        Tween.registerAccessor(Actor.class, new ActorAccessor());

        stage = new Stage();    
        Image image = new Image(new Texture(Gdx.files.internal("data/libgdx.png")));
        stage.addActor(image);

        Tween.to(image, ActorAccessor.ROTATE, 2.0f)
          .target(360)
          .repeatYoyo(100, 1)
          .start(getTweenManager());
    }   

    @Override
    public void dispose () {
        stage.dispose();
    }

    @Override
    public void render () {
        m_tween_manager.update(Gdx.graphics.getDeltaTime());     
        Gdx.gl.glClearColor(0.2f, 0.2f, 0.2f, 1);
        Gdx.gl.glClear(GL20.GL_COLOR_BUFFER_BIT);
        stage.act(Math.min(Gdx.graphics.getDeltaTime(), 1 / 30f));
        stage.draw();
    }

    @Override
    public void resize (int width, int height) {
        stage.getViewport().update(width, height, true);
    }

    @Override
    public void pause() {
    }

    @Override
    public void resume() {
    }
}
