private void startBlinking()
{
    Action blink = Actions.color(Colors.TEXT_INFO_BLINK);
    Action toNormalFast = Actions.color(Colors.TEXT_INFO_NORMAL, 0.3f);
    Action toNormalSlow = Actions.color(Colors.TEXT_INFO_NORMAL, 1.5f);
    Action pause = Actions.delay(4.5f);
    Action seq = Actions.sequence(pause,blink,toNormalFast,blink,toNormalSlow);
    addAction(Actions.forever(seq));
}
