/** Simple test case for mouse cursor hover over different labels to see different cursors and hotspots
 * pressed
 * @author haedri 
 * @author Simon Gerst*/
public class CursorIssueTest extends GdxTest {
    Cursor cursor1;
    Stage stage;
    Label pixmapLabel1;

    public void create () {
                Pixmap pixmap2 = new Pixmap(32, 32, Format.RGBA8888);
        pixmap2.setColor(Color.RED);
        pixmap2.fillCircle(16, 16, 8);
        cursor1 = Gdx.graphics.newCursor(pixmap2, 31, 31);
stage = new Stage(new ScreenViewport());
        Gdx.input.setInputProcessor(stage);
        BitmapFont font = new BitmapFont(Gdx.files.internal("data/arial-15.fnt"), false);
        pixmapLabel1 = new Label("Hover for Red Circle as Cursor, Hotspot: x[31], y[31]", new Label.LabelStyle(font, Color.WHITE));
        pixmapLabel1.addListener(new ClickListener() {

            @Override
            public void enter (InputEvent event, float x, float y, int pointer, Actor fromActor) {
                super.enter(event, x, y, pointer, fromActor);
                Gdx.graphics.setCursor(cursor1);
            }

            @Override
            public void exit (InputEvent event, float x, float y, int pointer, Actor fromActor) {
                super.enter(event, x, y, pointer, fromActor);
                Gdx.graphics.setCursor(null);
            }

        });

        Table hg = new Table();
        hg.setFillParent(true);
        hg.add(pixmapLabel1).top().center().row();
        stage.addActor(hg);
    }

    @Override
    public void render () {
        // set the clear color and clear the screen.
        Gdx.gl.glClearColor(0, 1, 1, 1);
        Gdx.gl.glClear(GL20.GL_COLOR_BUFFER_BIT);
        stage.draw();
        stage.act();
    }

    @Override
    public void resize (int width, int height) {
        stage.getViewport().update(width, height, true);
    }
}
