```
import com.badlogic.gdx.ApplicationAdapter;
import com.badlogic.gdx.Gdx;
import com.badlogic.gdx.graphics.Color;
import com.badlogic.gdx.graphics.GL20;
import com.badlogic.gdx.graphics.g2d.BitmapFont;
import com.badlogic.gdx.graphics.g2d.BitmapFontCache;
import com.badlogic.gdx.graphics.g2d.GlyphLayout;
import com.badlogic.gdx.graphics.g2d.freetype.FreeTypeFontGenerator;
import com.badlogic.gdx.scenes.scene2d.Stage;
import com.badlogic.gdx.scenes.scene2d.ui.Label;
import com.badlogic.gdx.scenes.scene2d.ui.Table;
import com.badlogic.gdx.utils.viewport.FitViewport;

public class FontWrapIssue extends ApplicationAdapter {

    private static final float GAME_WIDTH = 230.4f;

    private static final String CHARACTERS = "WMGCwATFLkJyrfIil";
    private static final int NUM_CHARACTERS = CHARACTERS.length();

    private Stage stage;

    private LabelHack label;

    private boolean searching = true;
    private long combination = NUM_CHARACTERS * NUM_CHARACTERS * NUM_CHARACTERS * NUM_CHARACTERS * NUM_CHARACTERS;

    public static void main (String[] args) throws Exception {
        new LwjglApplication(new FontWrapIssue());
    }

    @Override
    public void create () {
        float gameScale = GAME_WIDTH / Gdx.graphics.getWidth();
        int gameHeight = (int) (Gdx.graphics.getHeight() * gameScale + 0.5);
        stage = new Stage(new FitViewport(GAME_WIDTH, gameHeight));
        Gdx.input.setInputProcessor(stage);
        resize(Gdx.graphics.getWidth(), Gdx.graphics.getHeight());
        FreeTypeFontGenerator generator = new FreeTypeFontGenerator(Gdx.files.internal("data/arial.ttf"));
        FreeTypeFontGenerator.FreeTypeFontParameter parameter = new FreeTypeFontGenerator.FreeTypeFontParameter();
        parameter.size = (int) (20 / gameScale + 0.5);
        BitmapFont font = generator.generateFont(parameter);
        font.getData().setScale(gameScale);
        Label.LabelStyle labelStyle = new Label.LabelStyle();
        labelStyle.font = font;
        labelStyle.fontColor = Color.WHITE;
        Table table = new Table();
        table.setFillParent(true);
        addLabel(table, "", labelStyle);
        stage.addActor(table);
        table.debug();
    }

    private void addLabel(Table table, String text, Label.LabelStyle labelStyle) {
        label = new LabelHack(text, labelStyle);
        label.setWrap(true);
        table.add(label).fillX().expandX().row();

    }

    @Override
    public void render () {
        if (searching) {
            combination++;
            label.setText(String.format("test with %s %s issue", generateCombination(combination), generateCombination(combination)));
        }
        Gdx.gl.glClearColor(0, 0, 0, 1);
        Gdx.gl.glClear(GL20.GL_COLOR_BUFFER_BIT);
        stage.act(Gdx.graphics.getDeltaTime());
        stage.draw();
        if (searching) {
            int cacheLines = 0;
            for (GlyphLayout glyphLayout : label.getCache().getLayouts()) {
                cacheLines += glyphLayout.runs.size;
            }
            int boundLines = (int) (label.getHeight() / label.getStyle().font.getLineHeight() + 0.5);
            searching = (cacheLines == boundLines);
        }
    }

    private String generateCombination(long combination) {
        StringBuilder builder = new StringBuilder();
        boolean toggle = false;
        while (combination > 0) {
            int index = (int) (combination % NUM_CHARACTERS);
            if (toggle) index = NUM_CHARACTERS - 1 - index;
            toggle = !toggle;
            builder.append(CHARACTERS.charAt(index));
            combination /= NUM_CHARACTERS;
        }
        return builder.toString();
    }

    public static class LabelHack extends Label {

        public LabelHack(CharSequence text, LabelStyle style) {
            super(text, style);
        }

        public BitmapFontCache getCache() {
            return getBitmapFontCache();
        }
    }
}
