The latest merge of pull request #1751 prevents the following code inside Initialize() from ever happening on Windows:

```
// TODO: Implement multisampling (aka anti-alising) for all platforms!
            if (PreparingDeviceSettings != null)
            {
                GraphicsDeviceInformation gdi = new GraphicsDeviceInformation();
                gdi.GraphicsProfile = GraphicsProfile; // Microsoft defaults this to Reach.
                gdi.Adapter = GraphicsAdapter.DefaultAdapter;
                gdi.PresentationParameters = presentationParameters;
                PreparingDeviceSettingsEventArgs pe = new PreparingDeviceSettingsEventArgs(gdi);
                PreparingDeviceSettings(this, pe);
                presentationParameters = pe.GraphicsDeviceInformation.PresentationParameters;
                GraphicsProfile = pe.GraphicsDeviceInformation.GraphicsProfile;
            }

            // Needs be before ApplyChanges()
            _graphicsDevice = new GraphicsDevice(GraphicsProfile, presentationParameters);

    #if !MONOMAC
            ApplyChanges();
    #endif
```

`#endif // WINDOWS || WINRT` was moved to line 363, which is after the graphics device is created and ApplyChanges() is called.

If it is moved back up to after the line:

```
#endif // MONOMAC
```

then everything works fine again.

Hi,
I [previously filed a bug related to MGFX](https://github.com/mono/MonoGame/issues/1166) for DX/Windows 8.  I'm now trying to run the same Effect file through 2MGFX and use the output file on an Android game (using the `develop` branch).  Here's my Effect file:

```
// ------------------------------------------------------------------------------------------------ //
// ----- Copyright 2011 Christopher Harris --------------------- http://krypton.codeplex.com/ ----- //
// ----------------------------------------------------------------- mailto:xixonia@gmail.com ----- //
// ------------------------------------------------------------------------------------------------ //

// ------------------------------------------------------------------------------------------------ //
// ----- Parameters ------------------------------------------------------------------------------- //

float4x4 Matrix;

texture Texture0;
texture Texture1;
float2  LightPosition;
float   LightIntensityFactor = 1;

float   ShadowStrech = 1000000;

float2 TexelBias;

float4 AmbientColor;

float Bluriness = 0.5f;
float BlurFactorU = 0;
float BlurFactorV = 0;

// ------------------------------------------------------------------------------------------------ //
// ----- Samplers --------------------------------------------------------------------------------- //

sampler2D tex0 = sampler_state
{
    Texture = <Texture0>;
    AddressU = Clamp;
    AddressV = Clamp;
};

sampler2D tex1 = sampler_state
{
    Texture = <Texture1>;
    AddressU = Clamp;
    AddressV = Clamp;
};

// ------------------------------------------------------------------------------------------------ //
// ----- Structures ------------------------------------------------------------------------------- //

struct ShadowHullVertex
{
    float4 Position : POSITION;
    float2 Normal   : NORMAL0;
    float4 Color    : COLOR0;
};

struct VertexPositionColor
{
    float4 Position : POSITION;
    float4 Color    : COLOR0;
};

struct VertexPositionColorTexture
{
    float4 Position : POSITION;
    float4 Color    : COLOR0;
    float2 TexCoord : TEXCOORD0;
};

struct VertexPositionTexture
{
    float4 Position : POSITION;
    float2 TexCoord : TEXCOORD0;
};

struct Color2
{
    float4 Color0 : COLOR0;
    float4 Color1 : COLOR1;
};

// ------------------------------------------------------------------------------------------------ //
// ----- Vertex Shaders --------------------------------------------------------------------------- //

VertexPositionTexture VS_TextureNoTransform(VertexPositionTexture input)
{
    return input;
};

VertexPositionColorTexture VS_ColorTexture(VertexPositionColorTexture input)
{
    input.Position = mul(input.Position, Matrix);

    return input;
};

VertexPositionColor VS_Hull(ShadowHullVertex input)
{
    VertexPositionColor output;

    output.Position = mul(input.Position, Matrix);
    output.Color = input.Color;

    return output;
};

VertexPositionColor VS_Hull_RadialStretch(ShadowHullVertex input)
{
    float2 direction = normalize(LightPosition.xy - input.Position.xy);

    if(dot(input.Normal.xy, direction) < 0)
    {
        // Stretch backfacing vertices
        input.Position.xy -= direction * ShadowStrech;
    }

    VertexPositionColor output;

    output.Position = mul(input.Position, Matrix);
    output.Color = input.Color;

    return output;
};

// ------------------------------------------------------------------------------------------------ //
// ----- Pixel Shaders ---------------------------------------------------------------------------- //

// ------------------------------------------------------------------------------------------------ //
// ----- Techniques ------------------------------------------------------------------------------- //

float4 PS_Texture(in float2 texCoord : TEXCOORD0) : COLOR0
{
    return tex2D(tex0, texCoord + TexelBias);
};

float4 PS_ColorTexture(in float4 color : COLOR0, in float2 texCoord : TEXCOORD0) : COLOR0
{
    return tex2D(tex0, texCoord) * color;
};

float4 PS_LightTexture(VertexPositionColorTexture input) : COLOR0
{
    return pow(abs(tex2D(tex0, input.TexCoord)) * input.Color, LightIntensityFactor);
};

float4 PS_Color(in float4 color : COLOR0) : COLOR0
{
    return color;
};

float4 PS_White() : COLOR0
{
    return float4(1, 1, 1, 1);
};

float4 PS_Black() : COLOR0
{
    return float4(0, 0, 0, 1);
};

float4 PS_Debug() : COLOR0
{
    return float4(1, 0, 0, 1);
};

float4 PS_BlurH(in float2 texCoord : TEXCOORD0) : COLOR0
{
    float blurFactor = Bluriness * BlurFactorU / 4.0f;

    return
        tex2D(tex0, float2(texCoord.x - blurFactor * 4, texCoord.y) + TexelBias) * 0.05f +
        tex2D(tex0, float2(texCoord.x - blurFactor * 3, texCoord.y) + TexelBias) * 0.09f +
        tex2D(tex0, float2(texCoord.x - blurFactor * 2, texCoord.y) + TexelBias) * 0.12f +
        tex2D(tex0, float2(texCoord.x - blurFactor,     texCoord.y) + TexelBias) * 0.15f +
        tex2D(tex0, float2(texCoord.x,                  texCoord.y) + TexelBias) * 0.18f +
        tex2D(tex0, float2(texCoord.x + blurFactor,     texCoord.y) + TexelBias) * 0.15f +
        tex2D(tex0, float2(texCoord.x + blurFactor * 2, texCoord.y) + TexelBias) * 0.12f +
        tex2D(tex0, float2(texCoord.x + blurFactor * 3, texCoord.y) + TexelBias) * 0.09f +
        tex2D(tex0, float2(texCoord.x + blurFactor * 4, texCoord.y) + TexelBias) * 0.05f;
}

float4 PS_BlurV(in float2 texCoord : TEXCOORD0) : COLOR0
{
    float blurFactor = Bluriness * BlurFactorV / 4.0f;

    return
        tex2D(tex0, float2(texCoord.x, texCoord.y - blurFactor * 4) + TexelBias) * 0.05f +
        tex2D(tex0, float2(texCoord.x, texCoord.y - blurFactor * 3) + TexelBias) * 0.09f +
        tex2D(tex0, float2(texCoord.x, texCoord.y - blurFactor * 2) + TexelBias) * 0.12f +
        tex2D(tex0, float2(texCoord.x, texCoord.y - blurFactor)     + TexelBias) * 0.15f +
        tex2D(tex0, float2(texCoord.x, texCoord.y)                  + TexelBias) * 0.18f +
        tex2D(tex0, float2(texCoord.x, texCoord.y + blurFactor)     + TexelBias) * 0.15f +
        tex2D(tex0, float2(texCoord.x, texCoord.y + blurFactor * 2) + TexelBias) * 0.12f +
        tex2D(tex0, float2(texCoord.x, texCoord.y + blurFactor * 3) + TexelBias) * 0.09f +
        tex2D(tex0, float2(texCoord.x, texCoord.y + blurFactor * 4) + TexelBias) * 0.05f;
}

// ------------------------------------------------------------------------------------------------
// ----- Technique: TextureToTarget ---------------------------------------------------------------

technique TextureToTarget_Add
{
    pass Pass1
    {
        StencilEnable = false;

        BlendOp = Add;
        SrcBlend = One;
        DestBlend = One;

        AlphaBlendEnable = True;

        CullMode = CCW;

        #if SM4
        VertexShader = compile vs_4_0 VS_TextureNoTransform();
        PixelShader = compile ps_4_0 PS_Texture();
        #else
        VertexShader = compile vs_3_0 VS_TextureNoTransform();
        PixelShader = compile ps_3_0 PS_Texture();
        #endif

    }
};

technique TextureToTarget_Multiply
{
    pass Pass1
    {
        StencilEnable = false;
        BlendOp = Add; 

        SrcBlend = Zero;

#if SM4
        DestBlend = One;
#else
        DestBlend = SrcColor;
#endif

        AlphaBlendEnable = true;

        CullMode = CCW;

        #if SM4
        VertexShader = compile vs_4_0 VS_TextureNoTransform();
        PixelShader = compile ps_4_0 PS_Texture();
        #else
        VertexShader = compile vs_3_0 VS_TextureNoTransform();
        PixelShader = compile ps_3_0 PS_Texture();
        #endif
    }
};

technique LightTexture
{
    pass Pass1
    {
        StencilEnable = false;

        BlendOp = Add;
        DestBlend = Zero;
        SrcBlend = SrcAlpha;

        AlphaBlendEnable = true;

        #if SM4
        VertexShader = compile vs_4_0 VS_ColorTexture();
        PixelShader = compile ps_4_0 PS_LightTexture();
        #else
        VertexShader = compile vs_3_0 VS_ColorTexture();
        PixelShader = compile ps_3_0 PS_LightTexture();
        #endif
    }
};

// ------------------------------------------------------------------------------------------------
// ----- Technique: PointLight_Shadow -------------------------------------------------------------

technique PointLight_Shadow_Solid
{
    pass Shadow
    {
        StencilEnable = false;

        ScissorTestEnable = true;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = DestColor;
        DestBlend = Zero;

        //ColorWriteEnable = Alpha;

        #if SM4
        VertexShader = compile vs_4_0 VS_Hull_RadialStretch();
        PixelShader = compile ps_4_0 PS_Color();
        #else
        VertexShader = compile vs_3_0 VS_Hull_RadialStretch();
        PixelShader = compile ps_3_0 PS_Color();
        #endif
    }
};

technique PointLight_Shadow_Illuminated
{
    pass Shadow
    {
        StencilEnable = false;

        ScissorTestEnable = true;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = DestAlpha;
        DestBlend = Zero;

        //ColorWriteEnable = Alpha;

        #if SM4
        VertexShader = compile vs_4_0 VS_Hull_RadialStretch();
        PixelShader = compile ps_4_0 PS_Color();
        #else
        VertexShader = compile vs_3_0 VS_Hull_RadialStretch();
        PixelShader = compile ps_3_0 PS_Color();
        #endif
    }

    pass Hull
    {
        StencilEnable = false;

        ScissorTestEnable = true;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = One;
        DestBlend = Zero;

        //ColorWriteEnable = Alpha;

        #if SM4
        VertexShader = compile vs_4_0 VS_Hull();
        PixelShader = compile ps_4_0 PS_White();
        #else
        VertexShader = compile vs_3_0 VS_Hull();
        PixelShader = compile ps_3_0 PS_White();
        #endif
    }
};

technique PointLight_Shadow_Occluded
{
    pass Shadow
    {
        StencilEnable = true;
        StencilFunc = Always;
        StencilPass = IncrSat;
        StencilFail = Keep;

        ScissorTestEnable = true;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = DestAlpha;
        DestBlend = Zero;

        //ColorWriteEnable = Alpha;

        #if SM4
        VertexShader = compile vs_4_0 VS_Hull_RadialStretch();
        PixelShader = compile ps_4_0 PS_Color();
        #else
        VertexShader = compile vs_3_0 VS_Hull_RadialStretch();
        PixelShader = compile ps_3_0 PS_Color();
        #endif
    }

    pass Hull
    {
        StencilEnable = true;
        StencilFunc = Equal;
        StencilRef = 1;
        StencilPass = Keep;

        ScissorTestEnable = true;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = One;
        DestBlend = Zero;

        //ColorWriteEnable = Alpha;

        #if SM4
        VertexShader = compile vs_4_0 VS_Hull();
        PixelShader = compile ps_4_0 PS_White();
        #else
        VertexShader = compile vs_3_0 VS_Hull();
        PixelShader = compile ps_3_0 PS_White();
        #endif
    }
};

technique PointLight_Light
{
    pass Light
    {
        StencilEnable = false;

        ScissorTestEnable = false;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = DestAlpha;
        DestBlend = One;

        //ColorWriteEnable = Red | Green | Blue;

        #if SM4
        VertexShader = compile vs_4_0 VS_ColorTexture();
        PixelShader = compile ps_4_0 PS_LightTexture();
        #else
        VertexShader = compile vs_3_0 VS_ColorTexture();
        PixelShader = compile ps_3_0 PS_LightTexture();
        #endif
    }
};

technique ClearTarget_Alpha
{
    pass Pass1
    {
        StencilEnable = false;

        AlphaBlendEnable = true;
        BlendOp = Add;
        SrcBlend = One;
        DestBlend = One;

        ScissorTestEnable = true;

        //ColorWriteEnable = Red | Green | Blue | Alpha;

        #if SM4
        VertexShader = compile vs_4_0 VS_TextureNoTransform();
        PixelShader = compile ps_4_0 PS_Black();
        #else
        VertexShader = compile vs_3_0 VS_TextureNoTransform();
        PixelShader = compile ps_3_0 PS_Black();
        #endif
    }
};

// ------------------------------------------------------------------------------------------------
// ----- Technique: Blur --------------------------------------------------------------------------

technique Blur
{
    pass HorizontalBlur
    {
        ScissorTestEnable = false;
        StencilEnable = false;
        AlphaBlendEnable = false;

        CullMode = CCW;

        #if SM4
        VertexShader = compile vs_4_0 VS_TextureNoTransform();
        PixelShader = compile ps_4_0 PS_BlurH();
        #else
        VertexShader = compile vs_3_0 VS_TextureNoTransform();
        PixelShader = compile ps_3_0 PS_BlurH();
        #endif
    }

    pass VerticalBlur
    {
        ScissorTestEnable = false;
        StencilEnable = false;
        AlphaBlendEnable = false;

        CullMode = CCW;

        #if SM4
            VertexShader = compile vs_4_0 VS_TextureNoTransform();
            PixelShader = compile ps_4_0 PS_BlurV();
        #else
            VertexShader = compile vs_3_0 VS_TextureNoTransform();
            PixelShader = compile ps_3_0 PS_BlurV();
        #endif
    }
}

// ------------------------------------------------------------------------------------------------
// ----- Technique: DebugDraw ---------------------------------------------------------------------

technique DebugDraw
{
    pass Solid
    {
        StencilEnable = false;
        AlphaBlendEnable = false;

        #if SM4
        VertexShader = compile vs_4_0 VS_Hull();
        PixelShader = compile ps_4_0 PS_Debug();
        #else
        VertexShader = compile vs_3_0 VS_Hull();
        PixelShader = compile ps_3_0 PS_Debug();
        #endif
    }
};
```

