``` hlsl
float4x4 World;
float4x4 View;
float4x4 Projection;
float4x4 CameraWorld;

float4 CameraPosition;
float2 ViewportSize;

#define MAX_RAY_STEPS (50)
#define STEP_SIZE (1.0/MAX_RAY_STEPS)
#define VECTOR3_ZERO ((float3)0)

struct VertexPositionNormalTexture
{
    float4 Position : POSITION0;
    float4 Normal : NORMAL0;
    float2 Texture : TEXCOORD0;

    // TODO: add input channels such as texture
    // coordinates and vertex colors here.s
};


VertexPositionNormalTexture VertexShaderFunction(VertexPositionNormalTexture input)
{
    VertexPositionNormalTexture output = (VertexPositionNormalTexture)0; 

    //float4 pos = float4(input.Position.xy,-0.7,1);
    float4 pos = input.Position;
    matrix wvp = mul(mul(CameraWorld,View),Projection);

    output.Position = mul(pos,wvp);
    output.Texture = input.Texture;
    output.Normal = output.Position;

    /// store world position in .Normal
    output.Normal = input.Normal;

    return output;
}


bool SphereRayIntersect(float3 rayPos, float3 rayDir, float radius, out float t)
{
    //Compute A, B and C coefficients
    float a = dot(rayDir,rayDir);
    float b = 2 * dot(rayDir,rayPos);
    float c = dot(rayPos,rayPos) - (radius * radius);

    //Find discriminant
    float disc = b * b - 4 * a * c;

    // if discriminant is negative there are no real roots, so return 
    // false as ray misses sphere
    if (disc < 0)
        return false;

    // compute q as described above
    float distSqrt = sqrt(disc);
    float q;
    if (b < 0)
        q = (-b - distSqrt)/2.0;
    else
        q = (-b + distSqrt)/2.0;

    // compute t0 and t1
    float t0 = q / a;
    float t1 = c / q;

    // make sure t0 is smaller than t1
    if (t0 > t1)
    {
        // if t0 is bigger than t1 swap them around
        float temp = t0;
        t0 = t1;
        t1 = temp;
    }

    // if t1 is less than zero, the object is in the ray's negative direction
    // and consequently the ray misses the sphere
    if (t1 < 0)
        return false;

    // if t0 is less than zero, the intersection point is at t1
    if (t0 < 0)
    {
        t = t1;
        return true;
    }
    // else the intersection point is at t0
    else
    {
        t = t0;
        return true;
    }
}


float4 PixelShaderFunction(VertexPositionNormalTexture input, float2 screenPos : VPOS) : SV_Target
{
    float3 pos = CameraPosition.xyz;
    float3 dir = normalize(input.Normal.xyz-pos.xyz);

    return float4(dir,1);
}

technique Technique1
{
    pass Pass1
    {
        VertexShader = compile vs_3_0 VertexShaderFunction();
        PixelShader = compile ps_3_0 PixelShaderFunction();
    }
}
