using Microsoft.Xna.Framework;
using Microsoft.Xna.Framework.Graphics;
using System;
using System.Diagnostics;

namespace MonoGameLagTest
{
    public class Game1 : Game
    {
        GraphicsDeviceManager graphics;
        SpriteBatch spriteBatch;

```
    SpriteFont font;

    public Game1()
    {
        IsFixedTimeStep = false;
        graphics = new GraphicsDeviceManager(this);
        Content.RootDirectory = "Content";

        graphics.IsFullScreen = true;
        graphics.PreferredBackBufferWidth = 800;
        graphics.PreferredBackBufferHeight = 480;
        graphics.SupportedOrientations = DisplayOrientation.LandscapeLeft | DisplayOrientation.LandscapeRight;
        this.graphics.IsFullScreen = true;
        TimeSpan targetElapsedTime = base.TargetElapsedTime;
        base.MaxElapsedTime = TimeSpan.FromTicks(targetElapsedTime.Ticks * (long)2 + (long)1);
        base.IsMouseVisible = true;
        graphics.ApplyChanges();
    }

    protected override void Initialize()
    {
        base.Initialize();
    }

    protected override void LoadContent()
    {
        spriteBatch = new SpriteBatch(GraphicsDevice);

        font = Content.Load<SpriteFont>(@"TestFont");
    }

    protected override void UnloadContent()
    {
    }

    protected override void Update(GameTime gameTime)
    {
        base.Update(gameTime);
    }

    protected override void Draw(GameTime gameTime)
    {
        GraphicsDevice.Clear(Color.CornflowerBlue);
        spriteBatch.Begin();
        Debug.WriteLine((1.0d / gameTime.ElapsedGameTime.TotalSeconds).ToString());
        spriteBatch.DrawString(font, (1.0d / gameTime.ElapsedGameTime.TotalSeconds) + "fps", Vector2.Zero, Color.White);
        spriteBatch.End();

        base.Draw(gameTime);
    }
}
