#ifdef SM4
#define PROTOGAME_VERTEX_HIGH_SHADER vs_4_0_level_9_3
#define PROTOGAME_PIXEL_HIGH_SHADER ps_4_0_level_9_3
#define PROTOGAME_VERTEX_LOW_SHADER vs_4_0_level_9_1
#define PROTOGAME_PIXEL_LOW_SHADER ps_4_0_level_9_1
#define PROTOGAME_POSITION SV_Position
#define PROTOGAME_POSITION_INPUT POSITION0
#define PROTOGAME_POSITION_STATE SV_Position
#define PROTOGAME_DEPTH DEPTH0
#define PROTOGAME_TARGET(n) COLOR##n
#define PROTOGAME_NORMAL(n) NORMAL##n
#define PROTOGAME_TEXCOORD(n) TEXCOORD##n
#define PROTOGAME_BINORMAL(n) BINORMAL##n
#define PROTOGAME_TANGENT(n) TANGENT##n
#define PROTOGAME_BLENDWEIGHT(n) BLENDWEIGHT##n
#define PROTOGAME_BLENDINDICES(n) BLENDINDICES##n

#define PROTOGAME_DECLARE_TEXTURE(Name) \
    Texture2D<float4> Name; \
    sampler Name##Sampler

#define PROTOGAME_DECLARE_CUBEMAP(Name) \
    TextureCube<float4> Name; \
    sampler Name##Sampler

#define PROTOGAME_SAMPLER_NAME(Name) Name##Sampler
#define PROTOGAME_CUBEMAP_NAME(Name) Name##Sampler

#define PROTOGAME_SAMPLE_TEXTURE(Name, texCoord)  (Name.Sample(Name##Sampler, texCoord))
#define PROTOGAME_SAMPLE_CUBEMAP(Name, texCoord)  (Name.Sample(Name##Sampler, texCoord))

#else
#define PROTOGAME_VERTEX_HIGH_SHADER vs_3_0
#define PROTOGAME_PIXEL_HIGH_SHADER ps_3_0
#define PROTOGAME_VERTEX_LOW_SHADER vs_2_0
#define PROTOGAME_PIXEL_LOW_SHADER ps_2_0
#define PROTOGAME_POSITION_INPUT POSITION0
#define PROTOGAME_POSITION_STATE POSITION0
#define PROTOGAME_POSITION POSITION0
#define PROTOGAME_DEPTH DEPTH0
#define PROTOGAME_TARGET(n) COLOR##n
#define PROTOGAME_NORMAL(n) NORMAL##n
#define PROTOGAME_TEXCOORD(n) TEXCOORD##n
#define PROTOGAME_BINORMAL(n) BINORMAL##n
#define PROTOGAME_TANGENT(n) TANGENT##n
#define PROTOGAME_BLENDWEIGHT(n) BLENDWEIGHT##n
#define PROTOGAME_BLENDINDICES(n) BLENDINDICES##n

#define PROTOGAME_DECLARE_TEXTURE(Name) \
    sampler2D Name

#define PROTOGAME_DECLARE_CUBEMAP(Name) \
    samplerCUBE Name

#define PROTOGAME_SAMPLER_NAME(Name) Name
#define PROTOGAME_CUBEMAP_NAME(Name) Name

#define PROTOGAME_SAMPLE_TEXTURE(Name, texCoord)  (tex2D(Name, texCoord))
#define PROTOGAME_SAMPLE_CUBEMAP(Name, texCoord)  (texCUBE(Name, texCoord))

#endif

// -----------------------------------------------------------------------------
// A shader which combines the color map and light maps in deferred shading.
// -----------------------------------------------------------------------------

PROTOGAME_DECLARE_TEXTURE(Color) = sampler_state
{
    Texture = (Color);
    AddressU = CLAMP;
    AddressV = CLAMP;
    MagFilter = LINEAR;
    MinFilter = LINEAR;
    MipFilter = LINEAR;
};

PROTOGAME_DECLARE_TEXTURE(Light) = sampler_state
{
    Texture = (Light);
    AddressU = CLAMP;
    AddressV = CLAMP;
    MagFilter = LINEAR;
    MinFilter = LINEAR;
    MipFilter = LINEAR;
};

float4x4 World;
float4x4 View;
float4x4 Projection;

struct VertexShaderInput
{
    float4 Position : PROTOGAME_POSITION_INPUT;
    float2 TexCoord : PROTOGAME_TEXCOORD(0);
};

struct VertexShaderOutput
{
    float4 Position : PROTOGAME_POSITION_STATE;
    float2 TexCoord : PROTOGAME_TEXCOORD(0);
};

struct PixelShaderOutput
{
    float4 Color : PROTOGAME_TARGET(0);
};

VertexShaderOutput DefaultVertexShader(VertexShaderInput input)
{
    VertexShaderOutput output;

    float4 worldPosition = mul(input.Position, World);
    float4 viewPosition = mul(worldPosition, View);
    output.Position = mul(viewPosition, Projection);

    output.TexCoord = input.TexCoord;

    return output;
}

PixelShaderOutput DefaultPixelShader(VertexShaderOutput input)
{
    PixelShaderOutput output;

    // Sample diffuse color.
    float4 diffuseColor = PROTOGAME_SAMPLE_TEXTURE(Color, input.TexCoord);

    // Sample light color.
    float4 light = PROTOGAME_SAMPLE_TEXTURE(Light, input.TexCoord);

    // Get the color component of the light.
    float3 diffuseLight = light.rgb;

    // Alpha channel of light is unused.

    // Set the calculated light.
    output.Color = float4((diffuseColor.rgb * diffuseLight), 1);

    return output;
}

technique RENDER_PASS_TYPE_DEFERRED
{
    pass
    {
        VertexShader = compile PROTOGAME_VERTEX_LOW_SHADER DefaultVertexShader();
        PixelShader = compile PROTOGAME_PIXEL_LOW_SHADER DefaultPixelShader();
    }
}
```

When the shader is compiled, I get `InvalidOperationException` when MonoGame tries to compile the effect under OpenGL / MojoShader, because of this line:

``` csharp
                // We need the original sampler name... look for that in the symbols.
                var originalSamplerName =
                    symbols.First(e => e.register_set == MojoShader.MOJOSHADER_symbolRegisterSet.MOJOSHADER_SYMREGSET_SAMPLER &&
                    e.register_index == samplers[i].index
                ).name;
```

The reason this fails is because MojoShader is returning garbage in the samplers array.  While the first element of the array is correct, e.g:

![image](https://cloud.githubusercontent.com/assets/504826/15800817/9ff44cb8-2ac7-11e6-8193-04b6abf5df43.png)

The second sampler however is garbage data:

![image](https://cloud.githubusercontent.com/assets/504826/15800820/bb0ff36c-2ac7-11e6-8be9-6677f0b6c918.png)

Because index is nonsense, the `First` call fails.

This is probably a bug in MojoShader, and it looks like memory corruption of some nature.

