Rough example implementation:

```c#
class TraitDictionary
{
               // Needed only to be able to remove actors from all trait containers
		static readonly Dictionary<Type, ITraitContainer> sTraits = new Dictionary<Type, ITraitContainer>();

		public static void RegisterTraitContainer(Type type, ITraitContainer container)
		{
			sTraits.Add(type, container);
		}

		void InnerAdd(Actor actor, Type t, object val)
		{
			//InnerGet(t).Add(actor, val);
			var genericType = typeof(TraitContainer<>).MakeGenericType(t);
			var singletonField = genericType.GetField("Singleton", BindingFlags.Public | BindingFlags.Static);
			ITraitContainer singleton = (ITraitContainer) singletonField.GetValue(null);
			singleton.Add(actor, val);
		}

// All function should become static. Or better yet remove them and call TraitContainer directly.
		public T Get<T>(Actor actor)
		{
			CheckDestroyed(actor);
			//return InnerGet<T>().Get(actor.ActorID);
			return TraitContainer<T>.Singleton.Get(actor.ActorID);
		}

		public T GetOrDefault<T>(Actor actor)
		{
			CheckDestroyed(actor);
			//return InnerGet<T>().GetOrDefault(actor.ActorID);
			return TraitContainer<T>.Singleton.GetOrDefault(actor.ActorID);
		}

		public IEnumerable<T> WithInterface<T>(Actor actor)
		{
			CheckDestroyed(actor);
			//return InnerGet<T>().GetMultiple(actor.ActorID);
			return TraitContainer<T>.Singleton.GetMultiple(actor.ActorID);
		}

		public IEnumerable<TraitPair<T>> ActorsWithTrait<T>()
		{
			//return InnerGet<T>().All();
			return TraitContainer<T>.Singleton.All();
		}

		public IEnumerable<Actor> ActorsHavingTrait<T>()
		{
			//return InnerGet<T>().Actors();
			return TraitContainer<T>.Singleton.Actors();
		}

		public IEnumerable<Actor> ActorsHavingTrait<T>(Func<T, bool> predicate)
		{
			//return InnerGet<T>().Actors(predicate);
			return TraitContainer<T>.Singleton.Actors(predicate);
		}

		public int CalcSyncHash(ref int n)
		{
			//return InnerGet<ISync>().CalcSyncHash(ref n);
			return TraitContainer<ISync>.Singleton.CalcSyncHash(ref n);
		}

		public void RemoveActor(Actor a)
		{
			//foreach (var t in traits)
			foreach (var t in sTraits)
				t.Value.RemoveActor(a.ActorID);
		}


	
}
