```
'use babel';

import MyPackage from '../lib/my-package';

// Use the command `window:run-package-specs` (cmd-alt-ctrl-p) to run specs.
//
// To run a specific `it` or `describe` block add an `f` to the front (e.g. `fit`
// or `fdescribe`). Remove the `f` to unfocus the block.

describe('MyPackage', () => {
  let workspaceElement, activationPromise;
  let editor;

  beforeEach(() => {
    workspaceElement = atom.views.getView(atom.workspace);
    activationPromise = atom.workspace.open('../spec/test.we');
  });

  describe('when the my-package:beautify event is triggered', () => {
    it('can format the whole text this editor', () => {
      // Before the activation event the view is not on the DOM, and no panel
      // has been created

      // This is an activation event, triggering it will cause the package to be
      // activated.

      waitsForPromise(() => {
        return activationPromise.then(edit => editor=edit);
      });

      runs(() => {
        let text = atom.workspace.getActiveTextEditor().getText();

        console.log(atom.workspace.getActiveTextEditor().getPath());
        console.log(editor.getPath());

        atom.commands.dispatch(workspaceElement, 'my-package:beautify');

        expect(editor.getText()).not.toBe(text);
      });
    });
  });
});

