```
    for(int i = 0; i < sourceTotal_; i++){
        ALint state;
        ALuint source = _sources[i].sourceId;
        alGetSourcei(source, AL_SOURCE_STATE, &state);
        if (state != AL_PLAYING && state != AL_PAUSED) {
            sourceIndex = i;
