+    void Scale9Sprite::setGlobalZOrder(float globalZOrder)
+    {
+        Node::setGlobalZOrder(globalZOrder);
+        // apply to protected children too
+        for( auto &child: _protectedChildren)
+            child->setGlobalZOrder(globalZOrder);
+    }
