void Goods::inition(const std::string& filename, const std::string& efffilename, bool flag)
{
    this->initWithFile(filename);
    this->setPosition(200, 300);

    Node* effNode = CSLoader::createNode(efffilename);
    ActionTimeline *action = CSLoader::createTimeline(efffilename);
    action->gotoFrameAndPlay(0, true);
    effNode->runAction(action);
    this->addChild(effNode);

    auto size = effNode->getChildByName("diaoluo_gaoneng_1")->getContentSize();
    body = PhysicsBody::createCircle(size.width/2, PhysicsMaterial(0.0f,1.0f,0.0f), Vec2(0,0));
    body->setGroup(-1);
    this->setPhysicsBody(body);

    if (flag)
    {
        float velocityf = 0;
        velocityDirection = Vec2(0,1);
        body->setCollisionBitmask(0x400);   //0x01 0001 0x02 0010
        body->setCategoryBitmask(0xC00);
        body->setContactTestBitmask(0xC00);
        body->setGravityEnable(false);

        velocityf = getLeiBaoSuDu();
        float jiaodu = dropGoodsChuShiRatain();
        velocityDirection = velocityDirection.rotateByAngle(Vec2(0,0),CC_DEGREES_TO_RADIANS(jiaodu));
        velocityDirection *= velocityf;
        body->setVelocity(velocityDirection);
    }else
    {
        body->setVelocity(Vec2(0,0));
        body->setGravityEnable(false);
        body->setCollisionBitmask(0x0);
        body->setCategoryBitmask(0x0);
        body->setContactTestBitmask(0x0);   //Õ®÷™ƒ‹≤ªƒ‹≈ˆ◊≤
    }

    scheduleUpdate();
}
float Goods::getLeiBaoSuDu(){
    float randValue1 = 100;
    float randValue2 = 300;
    int randValue = randValue2 - randValue1;
    randValue = abs(rand()%randValue) + 1;
    return randValue1 + randValue;
}
float Goods::dropGoodsChuShiRatain(){
    int randValue = 360;
    randValue = abs(rand()%randValue) + 1;
    return randValue;
}
void Goods::update(float dt)
{
    Vec2 v = body->getVelocity();
    if (fabs(velocityDirection.x) != fabs(v.x) || fabs(velocityDirection.y) != fabs(v.y))
    {
        CCLOG("old: %f  %f  --  new: %f  %f", velocityDirection.x, velocityDirection.y, v.x, v.y);

    }
}
