```c++
#include "audio/include/AudioEngine.h"
#include <list>
#include <string>

using namespace std;
using namespace cocos2d::experimental;

static list<string> g_playList;
void enginePlay(const char *filename);
void playMusic(const char *filename)
{
    string music = filename;
    g_playList.push_back(music);
    if (g_playList.size() == 1) {
        enginePlay(filename);
    }
}

void enginePlay(const char *filename)
{

    int playID = AudioEngine::play2d(filename, false, 1);
    AudioEngine::setFinishCallback(playID, [](int finishID,const string& file){
        g_playList.pop_front();
        CCLOG("finish music %s",file.c_str());
        if (!g_playList.empty()) {
            auto it = g_playList.front();
            enginePlay(it.c_str());

        }

    });

}

void btnCallback(Ref* sender)
{
    playMusic("fonts/turnToNight.mp3");
    playMusic("fonts/turnToDay.mp3");
    playMusic("fonts/wolf.mp3");

}

```

