    std::string updateDir = FileUtils::getInstance()->getWritablePath() + "assetsmanager-test";
    log("updateDir: %s", updateDir.c_str());
    FileUtils::getInstance()->addSearchPath(updateDir, true);

    auto manager = AssetsManagerEx::create("project.manifest", updateDir);
    manager->retain();

    auto listener = EventListenerAssetsManagerEx::create(manager, [=](EventAssetsManagerEx* event){

        static const char* eventCodeStr[] = {
            "ERROR_NO_LOCAL_MANIFEST",
            "ERROR_DOWNLOAD_MANIFEST",
            "ERROR_PARSE_MANIFEST",
            "NEW_VERSION_FOUND",
            "ALREADY_UP_TO_DATE",
            "UPDATE_PROGRESSION",
            "ASSET_UPDATED",
            "ERROR_UPDATING",
            "UPDATE_FINISHED",
            "UPDATE_FAILED",
            "ERROR_DECOMPRESS"
        };

        log("event code: %s, assetID: %s, percent: %f", eventCodeStr[(int)event->getEventCode()], event->getAssetId().c_str(), event->getPercent());
        if (event->getEventCode() == EventAssetsManagerEx::EventCode::NEW_VERSION_FOUND)
        {
            manager->update();
        }
    });

    Director::getInstance()->getEventDispatcher()->addEventListenerWithSceneGraphPriority(listener, this);

    manager->checkUpdate();
