auto sprite = Sprite::createWithSpriteFrameName("progress-happiness.png");
sprite->setColor(Color3B(0, 0, 0));

_bar = ProgressTimer::create(sprite);
_bar->setType(ProgressTimer::Type::BAR);
_bar->setMidpoint(Vec2(0, 0.5));
_bar->setBarChangeRate(Vec2(1, 0));
_bar->setPercentage(50);
this->addChild(_bar);
