{
    vec4 normalColor = texture2D(u_texture, v_texCoord);
    vec4 alphaColor =  texture2D(alpha_texture, v_texCoord);
    vec4 mergeColor =  vec4(normalColor.r, normalColor.g, normalColor.b, alphaColor.r);
    gl_FragColor = v_fragmentColor * mergeColor;
}
