    set_angular_velocity(start_av)
    set_linear_velocity(start_lv)

func _ready():
    set_process_input(true)

func _input(event):
    if( event.type == InputEvent.KEY):
        if(event.scancode == KEY_R && event.pressed == false):
            startOver()
