const LEVELS_DIRECTORY = "res://levels"
static func readLevels():
    var dir = Directory.new()

    var paths = []
    dir.open(LEVELS_DIRECTORY);
    dir.list_dir_begin()
    for i in range(10):
        var p = dir.get_next()
        if p == "":
            break
        if p.find(".lvl") == -1:
            continue
        paths.append(p)

    return paths[0]
func _ready():
    var count = readLevels()
    get_node("Label").set_text(count)
