bool* CameraViewImGuiUI::ShowCameraViewport(void) {
	// ImGui Default
	bool * p_opened = NULL;

	// create tool bar
	ImGuiWindowFlags window_flags = 0;
	window_flags |= ImGuiWindowFlags_NoResize;

	// If the preview window is not initialized - Initialize it
	if (!m_bInitialized) {
		InitializePreviewScene();
	}

	// Create ImGui Window
	if (ImGui::Begin("Camera View", p_opened, ImVec2(WIDTH + 28, HEIGHT + 64),
			0.5, window_flags)) {

		// If not initialized position the window
		if (!m_bInitialized) {
			// Get Window Position
			ImVec2 WindowPosition = ImGui::GetWindowPos();

			// Set window position if it wasn't set in the pass
			if (WindowPosition.x == 0 && WindowPosition.y == 0) {
				// Get current window dimensions
				unsigned int WindowWidth = g_pApp->GetGraphics()->GetWidth();
				unsigned int WindowHeight = g_pApp->GetGraphics()->GetHeight();

				// Set window position
				ImGui::SetWindowPos(
						ImVec2((float) WindowWidth - WIDTH - 200,
								(float) WindowHeight - HEIGHT - 200));
			}
		}

		// Set three columns
		ImGui::SetCursorPosX(ImGui::GetWindowWidth() - 160);

		// Close Window
		if (ImGui::SmallButton("Snap")) {
			Snapshoot();
		}

		ImGui::SameLine();

		// Close Window
		if (ImGui::SmallButton("Reset")) {
			Reset();
		}

		ImGui::SameLine();

		// Close Window
		if (ImGui::SmallButton("Close")) {
			// Send a event
			VariantMap SendImGuiEvent;

			ImGuiInterface * pthisInterface = g_pApp->GetSubsystem<
					ImGuiInterface>();

			SendImGuiEvent[IMGUI_CAMERAVIEWWINDOW::P_BUTTON] = "Close";

			pthisInterface->SendEvent(EVENT_DATA_IMGUI_CAMERAVIEWWINDOW,
					SendImGuiEvent);
		}

		ImGui::Separator();

		// Show texture if camera has a view
		if (m_pCurrentCamera != NULL) {

			// Creates a texture
			ImGui::Image(m_pImGuiTextureID, ImVec2(WIDTH, HEIGHT), ImVec2(0, 0),
					ImVec2(1, 1), ImColor(255, 255, 255, 255),
					ImColor(255, 255, 255, 128));
		}
	}

	ImGui::End();

	// Set Initialized
	m_bInitialized = true;

	return p_opened;
}

// Set the editor View port
void CameraViewImGuiUI::SetEditorData(EditorData * editorData) {
	// Set the editor data
	m_pEditorData = editorData;
}

// Set set a camera to view
void CameraViewImGuiUI::SetCamera(CameraComponent * camera) {
	// If the Camera Viewport is not created then return
	if (!m_pCameraViewport)
		return;

// if current camera is null
	if (camera == NULL) {
		// Get Current Scene
		m_pCameraScene = m_pEditorData->GetEditorScene();

		// Get view active camera
		m_pCameraNode = m_pEditorData->GetActiveCamera();
		m_pCurrentCamera = m_pCameraNode->GetComponent<Camera>();

		// Set to the active camera
		m_pCameraViewport->SetCamera(m_pCurrentCamera);
	} else {
		// Set Viewport
		m_pCameraViewport->SetCamera(camera);

	}

	return;
}

// Create a a Preview Scene
void CameraViewImGuiUI::InitializePreviewScene(void) {
	// Set New Viewport and textures
	m_pCameraViewport = new Viewport(g_pApp->GetGameLogic()->GetContext());
	m_pRenderTexture = new Texture2D(g_pApp->GetGameLogic()->GetContext());
	m_pDepthTexture = new Texture2D(g_pApp->GetGameLogic()->GetContext());

	// if current camera is null
	if (m_pCurrentCamera == NULL) {
		// get the camera
		m_pCameraNode = m_pEditorData->GetActiveCamera();

		m_pCurrentCamera = m_pCameraNode->GetComponent<Camera>();
	}

	// Set Scene and Camera
	m_pCameraViewport->SetScene(m_pCameraScene);
	m_pCameraViewport->SetCamera(m_pCurrentCamera);

	// set defaults
	const unsigned int Height = 1440;
	const unsigned int Width = 900;

	// Set width and height
	const unsigned int outputHeight = OUTPUTHEIGHT;
	const unsigned int outputWidth = OUTPUTWIDTH;

	const unsigned int colorComponents = 4;

	// Create necessary texture
	m_pRenderTexture->SetSize(Height, Width, m_textureFormat,
			TEXTURE_RENDERTARGET);
	m_pDepthTexture->SetSize(Height, Width, Graphics::GetDepthStencilFormat(),
			TEXTURE_DEPTHSTENCIL);

	// Set parameneters
	m_pRenderTexture->SetFilterMode(FILTER_NEAREST);
	m_pDepthTexture->SetFilterMode(FILTER_NEAREST);

	RenderSurface * m_pRenderSurface = m_pRenderTexture->GetRenderSurface();

	// Set Render Surface
	m_pRenderSurface->SetViewport(0, m_pCameraViewport);
	m_pRenderSurface->SetUpdateMode(SURFACE_UPDATEALWAYS);
	m_pRenderSurface->SetLinkedDepthStencil( m_pDepthTexture->GetRenderSurface());


	// Set ID to match the texture of RenderTexture
	m_pImGuiTextureID = (void *) (intptr_t) m_pRenderTexture;


	return;
}

void CameraViewImGuiUI::CloseViewport(void) {
	// safe delete the texture
	SAFE_DELETE(m_pRenderTexture);
	SAFE_DELETE(m_pDepthTexture);

	// safe delte not used
	SAFE_DELETE(m_pSnapRenderTexture);
	SAFE_DELETE(m_pSnapDepthTexture);

}

void CameraViewImGuiUI::SetScene(Scene * CurrentScene) {
	m_pCameraScene = CurrentScene;

}

// Create a a Preview Scene
void CameraViewImGuiUI::Reset(void) {
// Get Current Scene
	m_pCameraScene = m_pEditorData->GetEditorScene();

	// Get view active camera
	m_pCameraNode = m_pEditorData->GetActiveCamera();
	m_pCurrentCamera = m_pCameraNode->GetComponent<Camera>();

	// Set to the active camera
	m_pCameraViewport->SetCamera(m_pCurrentCamera);

}

// Create a a Preview Scene
void CameraViewImGuiUI::Snapshoot(void) {
	// prevent executing if no viewport
	if(!m_pCameraViewport)
	{
		return;
	}

	// Get File System
	FileSystem * pFileSystem = g_pApp->GetFileSystem();

	// Get CurrentPath
	String CurrentPath = pFileSystem->GetCurrentDir();

	// Crate fileResulte vector
	Vector<String> fileResult;

	// Scan Dir
	pFileSystem->ScanDir(fileResult, CurrentPath, "SnapShoot*.png", SCAN_FILES, false);

	// Create a filename
	String newFilename = "SnapShoot"+String((unsigned int)fileResult.Size())+".png";

	// Queue update - Queue not needed but pass to force update
	RenderSurface * renderSurface = m_pRenderTexture->GetRenderSurface();
	renderSurface->QueueUpdate();

	// New Image
	Image * OutputImage(new Image(context_));

	// Set the output image
	OutputImage->SetSize(OUTPUTWIDTH,OUTPUTHEIGHT,4);

	// Create new image base on default height width
	unsigned char* ImageData = new unsigned char[OUTPUTWIDTH*OUTPUTHEIGHT*4];
	m_pRenderTexture->GetData(0, ImageData);

	//Save Delete
	OutputImage->SetData((unsigned char *)ImageData);

	// Save Image
	OutputImage->SavePNG(newFilename);

	// Safe delete memory
	delete ImageData;
	delete OutputImage;

}


